import React, { useState } from 'react'
import { Button, Form, FormGroup, Label, Input, Container } from 'reactstrap'

import api from '../../services/api'

const Register = ({ history }) => {
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [firstName, setFirstName] = useState('')
    const [lastName, setLastName] = useState('')

    const handleSubmit = async event => {
        event.preventDefault()
        console.log('result of the submit', email, password)

        const response = await api.post('/login', { email, password, firstName, lastName })
        const userId = response.data._id || false

        if (userId) {
            localStorage.setItem('user', userId)
            history.push('/dashboard')
        } else {
            const { message } = response.data
            console.log(message)
        }
    }

    return (
        <Container>
            <h2> Register: </h2>
            <p> Please <strong>register</strong> for a new account</p>
            <Form onSubmit={handleSubmit}>
                <FormGroup>
                    <Input type="text" name="firstname" id="firstName" placeholder="First name" onChange={event => setFirstName(event.target.value)} />
                </FormGroup>
                <FormGroup>
                    <Input type="text" name="lastname" id="lastName" placeholder="Last name" onChange={event => setLastName(event.target.value)} />
                </FormGroup>
                <FormGroup>
                    <Input type="email" name="email" id="exampleEmail" placeholder="Email" onChange={event => setEmail(event.target.value)} />
                </FormGroup>
                <FormGroup>
                    <Button className="submit-btn">Register</Button>
                </FormGroup>
                <FormGroup>
                    <Button className="secondary-btn" onClick={() => history.push('/login')}>Login instead?</Button>
                </FormGroup>
            </Form>
        </Container>
    )
}

export default Register
